import React, { Component } from "react";
import { Produto, P, PequenaInfo } from "./styles";

// Importando os dados da API
const request = new XMLHttpRequest();
const externUrl =
  "https://ed87c2a9-bcc4-4e0c-8fd2-fefb9875b65b.mock.pstmn.io/getStockConsolidation";
request.open("GET", externUrl, true);
request.onload = function() {
  var data = JSON.parse(this.response);
  console.log(data.data.products);

  data.data.products.forEach(p => {
    console.log(p);
  });
};
request.send();

class Asset extends Component {
  state = {
    productName: "Produto",
    equity: 0,
    amount: 0,
    averagePrice: 0,
    lastQuotation: 0,
    currentMonthYield: 0,
    lastTwelveMonthYeld: 0
  };

  render() {
    return (
      <React.Fragment>
        <Produto>
          <P fontSize={12}>{this.state.productName}</P>

          <PequenaInfo>
            <P fontSize={8}>SALDO ATUAL</P>
            <P fontSize={14}>
              R$ {this.state.amount * this.state.averagePrice}
            </P>
          </PequenaInfo>

          <PequenaInfo>
            <P fontSize={8}>QUANT.</P>
            <P fontSize={14}>{this.state.amount}</P>
          </PequenaInfo>

          <PequenaInfo>
            <P fontSize={8}>PREÇO MÉDIO</P>
            <P fontSize={14}>R$ {this.state.averagePrice}</P>
          </PequenaInfo>

          <PequenaInfo>
            <P fontSize={8}>ÚLTIMA COTAÇÃO</P>
            <P fontSize={14}>R$ {this.state.lastQuotation}</P>
          </PequenaInfo>

          <PequenaInfo>
            <P fontSize={8}>
              YIELD <span>(1M)</span>
            </P>
            <P fontSize={14}>{this.state.currentMonthYield}%</P>
          </PequenaInfo>

          <PequenaInfo>
            <P fontSize={8}>
              YIELD <span>(12M)</span>
            </P>
            <P fontSize={14}>{this.state.lastTwelveMonthYeld}%</P>
          </PequenaInfo>

          <PequenaInfo>
            <P fontSize={8}>% CARTEIRA</P>
            <P fontSize={14}>{this.state.equity}%</P>
          </PequenaInfo>
        </Produto>
        <Line />
      </React.Fragment>
    );
  }
}

export default Asset;
