import React, { Component } from "react";
import { Produto, P, PequenaInfo } from "./styles";

// Importando os dados da API
const request = new XMLHttpRequest();
const externUrl =
  "https://ed87c2a9-bcc4-4e0c-8fd2-fefb9875b65b.mock.pstmn.io/getStockConsolidation";
request.open("GET", externUrl, true);
request.onload = function() {
  var data = JSON.parse(this.response);
  console.log(data.data.products);

  data.data.products.forEach(p => {
    console.log(p);
  });
};
request.send();

class Asset extends Component {
  state = {
    productName: "Produto",
    equity: 0,
    amount: 0,
    averagePrice: 0,
    lastQuotation: 0,
    currentMonthYield: 0,
    lastTwelveMonthYeld: 0
  };

  render() {
    return (
      <Produto>
        <p>{this.state.productName}</p>

        <PequenaInfo>
          <P fontSize={10}>SALDO ATUAL</P>
          <P fontSize={14}>R$ {this.state.amount * this.state.averagePrice}</P>
        </PequenaInfo>

        <PequenaInfo>
          <P>QUANT.</P>
          <P>{this.state.amount}</P>
        </PequenaInfo>

        <PequenaInfo>
          <P>PREÇO MÉDIO</P>
          <P>R$ {this.state.averagePrice}</P>
        </PequenaInfo>

        <PequenaInfo>
          <P>ÚLTIMA COTAÇÃO</P>
          <P>R$ {this.state.lastQuotation}</P>
        </PequenaInfo>

        <PequenaInfo>
          <P>
            YIELD <span>(1M)</span>
          </P>
          <P>{this.state.currentMonthYield}%</P>
        </PequenaInfo>

        <PequenaInfo>
          <P>
            YIELD <span>(12M)</span>
          </P>
          <P>{this.state.lastTwelveMonthYeld}%</P>
        </PequenaInfo>

        <PequenaInfo>
          <P>% CARTEIRA</P>
          <P>{this.state.equity}%</P>
        </PequenaInfo>
      </Produto>
    );
  }
}

export default Asset;
