import React, { Component } from "react";
import styled from "styled-components";

const H4 = styled.h4`
  font-family: "helvetica-neue, arial";
  font-size: 10px;
  color: #707b81;
`;
function reverseString(strg) {
  let newString = "";

  for (let i = strg.length - 1; i >= 0; i--) {
    newString += strg[i];
  }

  return newString;
}
function styleMoney(money) {
  const moneyString = money.toString();
  let countNumbers = 0;
  let moneyFinal = "";

  for (let i = moneyString.length - 1; i >= 0; i--) {
    countNumbers++;

    if (moneyString[i] === ".") {
      moneyFinal += ",";
      countNumbers = 0;
    } else {
      moneyFinal += moneyString[i];
    }

    if (countNumbers === 3) {
      moneyFinal += ".";
      countNumbers = 0;
    }
  }

  return reverseString(moneyFinal);
}

console.log(styleMoney(12709.14));

class BotaoAcoes extends Component {
  state = { saldoBruto: 12709.14 };
  render() {
    return (
      <span>
        <h4>SALDO BRUTO</h4>
        <p>R$ {this.state.saldoBruto}</p>
      </span>
    );
  }
}

export default BotaoAcoes;
